SELECT

FROM
    advert_group ad_group


        AND ad_group.group_name = #{groupName,jdbcType=VARCHAR}


        AND ad_group.customer_id = #{customerId,jdbcType=VARCHAR}


        AND ad_group.day_budget = #{dayBudget,jdbcType=DECIMAL}


        AND ad_group.delivery_date = #{deliveryDate,jdbcType=VARCHAR}


        AND ad_group.status = #{status,jdbcType=TINYINT}


        AND ad_group.create_time = #{createTime,jdbcType=TIMESTAMP}


        AND ad_group.update_time = #{updateTime,jdbcType=TIMESTAMP}


        AND ad_group.platform = #{platform,jdbcType=VARCHAR}


        AND ad_group.pause_time = #{pauseTime,jdbcType=TIMESTAMP}


        AND ad_group.pause_num = #{pauseNum,jdbcType=INTEGER}


        AND ad_group.send_status = #{sendStatus,jdbcType=TINYINT}


        AND ad_group.service_line_code = #{serviceLineCode,jdbcType=VARCHAR}


        AND ad_group.total_pv = #{totalPv,jdbcType=BIGINT}


        AND ad_group.total_click = #{totalClick,jdbcType=INTEGER}


        AND ad_group.total_cost = #{totalCost,jdbcType=DECIMAL}


        AND ad_group.today_pv = #{todayPv,jdbcType=INTEGER}


        AND ad_group.today_click = #{todayClick,jdbcType=INTEGER}


        AND ad_group.today_cost = #{todayCost,jdbcType=DECIMAL}




ORDER BY
    ad_group.create_time DESC
LIMIT
    ${skip}, ${size}

